import React from 'react';
import './intro.css';
import bg from '../../assets/kermit-big.png'
import { Link } from 'react-scroll';
import btnImg from '../../assets/briefcase.png'
const Intro = () => {

  return (
    <section className="" id="intro">
        <div className="introContent">
            <span className="hello">Hello,</span>
            <span className="introText">I'm <span className="introName">Luke</span> <br />Website Desiginer</span>
            <p className="introPara">I am a skilled and passionate web designer with experience in creating visually appealing and user-friendly websites.</p>
            <Link><button className="btn"><img src={btnImg} alt="Hire Me"  />Hire me</button></Link>
        </div>
        <img src={bg} alt="" className="bg" />
    </section>
  )
}

export default Intro
